import React, { useState } from 'react';
const Todo = () => {
    const [Id, setUser] = useState('');
    const [Status, setEmail] = useState('');
    const handleSubmit = (e) => {
        e.preventDefault();  // Prevent form submission on enter or on form submit action
        const userData = {
            Id: Id,
            Status: Status
        };
        // Send data to the backend via POST
        fetch('http://localhost:3001/user', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        })
        .then(response => response.json())
        .then(data => console.log('Success:', data))
        .catch((error) => {
            console.error('Error:', error);
        });
    };
    return (
        <div>
            <Navbar/>
            <form onSubmit={handleSubmit}> {/* This still handles the form submission through Enter key */}
                <input type="text" placeholder="Enter your Id" onChange={(e) => setUser(e.target.value)} value={Id} required />
                <input type="email" placeholder="Enter your Status" onChange={(e) => setEmail(e.target.value)} value={Status} required />
                <button type="button" onClick={handleSubmit}>Submit</button> {/* Using type="button" to avoid form submission */}
            </form>
        </div>
    );
}
export default Todo
