  }
  const incrementCount = () => {
    setCount(prevCount => prevCount - 1);
  }
  return (
    <div>
      <h1> Vaanakam</h1>
      <div className="button-container">
        <button onClick={decrementCount}> - </button>
        <span> {count} </span>
        <button onClick={incrementCount}> + </button>
      </div>

    </div>
  );
}




