import React, { useState } from 'react';
const FetchingForm = () => {
    const [user, setUser] = useState('');
    const [email, setEmail] = useState('');
    const handleSubmit = (e) => {
        e.preventDefault();  // Prevent form submission on enter or on form submit action
        const userData = {
            user: user,
            email: email
        };
        // Send data to the backend via POST
        fetch('http://localhost:3001/submit-form', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        })
        .then(response => response.json())
        .then(data => console.log('Success:', data))
        .catch((error) => {
            console.error('Error:', error);
        });
    };
    return (
        <div>
            <form onSubmit={handleSubmit}> {/* This still handles the form submission through Enter key */}
                <input type="text" placeholder="Enter your name" onChange={(e) => setUser(e.target.value)} value={user} required />
                <input type="email" placeholder="Enter your email" onChange={(e) => setEmail(e.target.value)} value={email} required />
                <button type="button" onClick={handleSubmit}>Submit</button> {/* Using type="button" to avoid form submission */}
            </form>
        </div>
    );
}
export default FetchingForm;
